  login: {
    display:'flex',
    flexDirection:'column',
    justifyContent:"center",
    alignItems:'center',
    position: "relative",
  },
  loginscreen: {
    backgroundColor: Color.darkslategray,
    width: '100%',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default LoginScreen;
