import * as React from "react";
import { Text, StyleSheet, View, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { Border, FontSize, Color, FontFamily } from "../GlobalStyles";

const LoginScreen = () => {
  const navigation = useNavigation();

  return (
    <View style={styles.loginscreen}>
      <View style={styles.login}>
        <Text style={styles.title}>Cleber.io</Text>
        <Pressable
          style={styles.button}
          onPress={() => navigation.navigate("MainScreen")}
        >
          <View style={[styles.buttonbg, styles.divLayout1]} />
          <Text style={[styles.btntext, styles.btntextTypo]}>Entrar</Text>
        </Pressable>
        <View style={[styles.inputuser, styles.divLayout]}>
          <View style={[styles.div, styles.divLayout]} />
          <Text style={[styles.placeholder, styles.btntextTypo]}>Username</Text>
        </View>
        <View style={[styles.inputpass, styles.divLayout]}>
          <View style={[styles.div, styles.divLayout]} />
          <Text style={[styles.placeholder, styles.btntextTypo]}>Password</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  divLayout1: {
    borderRadius: Border.br_3xs,
    top: 0,
  },
  btntextTypo: {
    fontSize: FontSize.size_mini,
    textAlign: "left",
    color: Color.white,
    position: "absorelatlute",
  },
  divLayout: {
    height: 49,
    left: 0,
    width: 231,
    position: "relative",
  },
  title: {
    fontSize: 45,
    fontWeight: "900",
    fontFamily: FontFamily.poppinsBlack,
    textAlign: "center",
    color: Color.white,
    position: "relative",
  },
  buttonbg: {
    backgroundColor: Color.lightgreen,
    left: 0,
    borderRadius: Border.br_3xs,
    height: 36,
    width: 106,
    position: "relative",
  },
  btntext: {
    fontFamily: FontFamily.poppinsRegular,
  },
  button: {
    width: 106,
    position: "absolute",
  },
  div: {
    borderStyle: "solid",
    borderColor: "#66e097",
    borderWidth: 1,
    borderRadius: Border.br_3xs,
    top: 0,
  },
  placeholder: {
    textAlign:'center',
    fontWeight: "500",
    fontFamily: FontFamily.poppinsMedium,
  },
  login: {
    display:'flex',
    flexDirection:'column',
    justifyContent:"center",
    alignItems:'center',
    position: "relative",
  },
  loginscreen: {
    backgroundColor: Color.darkslategray,
    width: '100%',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default LoginScreen;
