import React, { useEffect, useState } from "react";
import { StyleSheet, View, Text, Pressable, TextInput, Image } from "react-native";
import { Color, FontFamily, FontSize, Border } from "../GlobalStyles";
import cardsData from "../cardsList.json";

const MainScreen = () => {
  const [cards, setCards] = useState(cardsData);

  const fetchCards = async () => {
    try {
      const response = await fetch(".../api/Thrashs");
      if (response.ok) {
        const data = await response.json();
        setCards(data);
      } else {
        console.error("Failed to fetch cards");
      }
    } catch (error) {
      console.error("Error fetching cards:", error);
    }
  };

  const handleLockClick = async (thrashId) => {
    try {
      const response = await fetch(`.../api/User/${userId}/${thrashId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        // You can add the payload or data you want to update here
      });

      if (response.ok) {
        // Handle successful update (e.g., update UI)
      } else {
        console.error("Failed to update card");
      }
    } catch (error) {
      console.error("Error updating card:", error);
    }
  };

  /*useEffect(() => {
    fetchCards();
  }, []);*/

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Cleber.io</Text>
        <Pressable style={styles.adminButton} onPress={() => navigation.navigate("")}>
          <Text style={styles.adminButtonText}>Adm</Text>
        </Pressable>
      </View>

      <View style={styles.searchBar}>
        <View style={styles.inputContainer}>
          <TextInput style={styles.input} placeholder="Pesquisar..." />
        </View>
      </View>

      {/* List of Cards */}
      <View style={styles.cardList}>
        {cardsData.map((card) => (
          <View key={card.id} style={styles.cardItem}>
            {/* Render card details */}
            <View style={styles.cardHeader}>
              <Text>ID: {card.id}</Text>
              <Pressable onPress={() => handleLockClick(card.id)}>
                <Image style={styles.trashIcon} source={require("../assets/trash@3x.png")} />
              </Pressable>
            </View>
            {card.openned ? (
              <Image style={styles.padlockOpenedIcon} source={require("../assets/padlockopenned@3x.png")} />
            ) : (
              <Image style={styles.padlockClosedIcon} source={require("../assets/padlockclosed@3x.png")} />
            )}
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: Color.white,
  },
  header: {
    backgroundColor: Color.darkslategray,
    height: 60,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 16,
  },
  title: {
    color: Color.white,
    fontWeight: "900",
    fontFamily: FontFamily.poppinsBlack,
    fontSize: FontSize.size_xl,
  },
  adminButton: {
    borderRadius: 5,
    backgroundColor: Color.lightgreen,
    width: 75,
    height: 25,
    justifyContent: "center",
    alignItems: "center",
  },
  adminButtonText: {
    color: Color.white,
    fontSize: 12,
    fontWeight: "600",
    fontFamily: FontFamily.poppinsSemiBold,
  },
  searchBar: {
    paddingHorizontal: 16,
    paddingTop: 12,
    marginVertical: 10,
  },
  inputContainer: {
    backgroundColor: Color.gray,
    borderRadius: Border.br_3xs,
    height: 45,
    shadowOpacity: 0.5,
    elevation: 6,
    shadowRadius: 6,
    shadowOffset: {
      width: 2,
      height: 2,
    },
  },
  input: {
    flex: 1,
    fontSize: FontSize.size_mini,
    fontWeight: "300",
    fontFamily: FontFamily.poppinsLight,
    color: Color.darkslategray,
    paddingHorizontal: 8,
  },
  cardList: {
    paddingHorizontal: 16,
  },
  cardItem: {
    backgroundColor: Color.gray,
    borderRadius: Border.br_3xs,
    marginBottom: 16,
    flexDirection: "column",
    alignItems: "center",
  },
  cardHeader: {
    flexDirection: "row",
    alignItems: "center",
  },
  trashIcon: {
    maxWidth: "33.63%",
    aspectRatio: 1,
    marginRight: 8,
  },
  padlockOpenedIcon: {
    width: 40,
    height: 40,
  },
  padlockClosedIcon: {
    width: 40,
    height: 40,
  },
});

export default MainScreen;
